import React, { useState } from "react";
import Navbar from "./components/navbar";
import Product from "./components/Product";

const App = () => {
  const [credit, setCredit] = useState(10000);

  const handleOrder = (price) => {
    if (credit >= price) {
      alert("Order placed successfully!");
      setCredit(credit - price);
    } else {
      alert("Insufficient credit!");
    }
  };

  return (
    <div>
      <Navbar credit={credit} />
      <div className="container">
        <Product
          name="iPhone 15"
          price={400}
          image="data:image/jpeg;base64,"  
          onOrder={handleOrder}
        />
        <Product
          name="MacBook"
          price={1500}
          image="data:image/jpeg;base64,"  
          onOrder={handleOrder}
        />
        <Product
          name="TV"
          price={700}
          image="data:image/jpeg;base64,"
          onOrder={handleOrder}
        />
        <Product
          name="Keyboard"
          price={100}
          image="data:image/jpeg;base64,"  
          onOrder={handleOrder}
        />
        <Product
          name="Smart Watch"
          price={200}
          image="data:image/jpeg;base64,"  
          onOrder={handleOrder}
        />
        <Product
          name="Headphones"
          price={150}
          image="data:image/jpeg;base64,"  
          onOrder={handleOrder}
        />
      </div>
      <button id="checkout">check out?</button>
    </div>
  );
};

export default App;
